WorkflowProcessEvent.eventDescription:
function afterProcessCreate(processId){

	log.error('**** afterProcessCreate ');
	hAPI.setCardValue("nroSolicitud", processId);

}

function beforeCancelProcess(colleagueId,processId){
	
	var ejb = new javax.naming.InitialContext().lookup("java:global/raf-inversioncomercial-backend/CartaAcuerdoEJB");
	
	log.error('**** beforeCancelProcess ');
	hAPI.setCardValue("tarea", "SOLICITUD CANCELADA");
	
	ejb.actualizarEstado(java.lang.Integer.parseInt(processId), "SOLICITUD CANCELADA", "CANCELADA");
	
}

function afterProcessFinish(processId){
	
	var companyId = getValue("WKCompany");
	var carpetaAdjuntosId = hAPI.getCardValue("carpetaAdjuntosId");
		
	var arq = arqMarvinLoad("v1", {
		prop: "com.arquimeda.marvin.server.js.Properties-v1"
	});
	arq.prop.load({
		datasetName: "parametros"
	});
	
	var login = arq.prop.get("RAF09.usuarioSistema.login");
	var password = arq.prop.get("RAF09.usuarioSistema.pass");
	var sysCollegueId = arq.prop.get("RAF09.usuarioSistema.id");
		
	ged.updateFolder({
		user: login,
		password: password,
		colleagueId: sysCollegueId,
		companyId: companyId,
		
		docId: carpetaAdjuntosId,
		security: [
		    {
				permission: true, 
				attributionType: "ALL", 
				attributionValue: "all", 
				securityLevel: "NONE"
		    }
		]
	});

}

	function beforeTaskSave(colleagueId,nextSequenceId,userList){
		
		var nroProceso = getValue("WKNumProces");
		var completo = getValue('WKCompletTask');
		var tarea = getValue("WKNumState");
		var userId = getValue("WKUser");
		var comentarios = getValue("WKUserComment");
		
		var ejb = new javax.naming.InitialContext().lookup("java:global/raf-inversioncomercial-backend/CartaAcuerdoEJB");
		
		log.error('**** BeforeTaskSave RAF09; userId: ' + userId + '; tarea: ' + tarea + '; completo? : ' + completo + '; nextSequenceId:' + nextSequenceId);
		
		var tareas = {
				'IniciarSolicitud': 0,
				'Inicial': 4,
				'AuditarCA' : 5,
				'CompletarCA' : 10,
				'RevisarCA' : 12,
				'Finalizada' : 17,
				'Rechazada' : 21,
				'CargarCA' : 25,
			};
		
		if (tarea == tareas.Inicial && completo == 'true') {
			var dateParser = new java.text.SimpleDateFormat("yyyy-MM-dd");
	
			log.error('**** BeforeTaskSave Inicial  ');
			hAPI.setCardValue("fechaSolicitud", dateParser.format(new Date()));
		}
		
		var estado = "PENDIENTE";
		if (tarea == tareas.Inicial && completo == 'true') {
			hAPI.setCardValue("tarea", "INICIAR SOLICITUD");
		}else if (tarea == tareas.CargarCA && nextSequenceId == tareas.CompletarCA && completo == "true") {
			hAPI.setCardValue("tarea", "COMPLETAR (APM)");
		}else if (tarea == tareas.CompletarCA && nextSequenceId == tareas.RevisarCA && completo == "true") {
			hAPI.setCardValue("tarea", "REVISAR (GTE DISTRITO)");
		}else if (tarea == tareas.RevisarCA && nextSequenceId == tareas.AuditarCA && completo == "true") {
			hAPI.setCardValue("tarea", "AUDITAR");
		}else if (tarea == tareas.AuditarCA && nextSequenceId == tareas.Finalizada && completo == "true") {
			hAPI.setCardValue("tarea", "SOLICITUD APROBADA");
			estado = "AUDITADA";
			sendMail("RAF09-Auditada");
			
			var requestFolderId = createRequestFolder();
			uploadAttachmentsToGed(requestFolderId);
			
		}else if (tarea == tareas.AuditarCA && nextSequenceId == tareas.Rechazada && completo == "true") {
			hAPI.setCardValue("tarea", "SOLICITUD RECHAZADA");
			estado = "RECHAZADA";
		}else if (tarea == tareas.AuditarCA && nextSequenceId == tareas.RevisarCA && completo == "true") {
			hAPI.setCardValue("tarea", "REVISAR (GTE DISTRITO)");
		}else if (tarea == tareas.RevisarCA && nextSequenceId == tareas.CompletarCA && completo == "true") {
			hAPI.setCardValue("tarea", "COMPLETAR (APM)");
		}else if (tarea == tareas.CargarCA && nextSequenceId == tareas.AuditarCA && completo == "true") {
			
			if(hAPI.listAttachments().size() == 0){
				throw "Debe adjuntar al menos un archivo.";
			}else{
				hAPI.setCardValue("tarea", "AUDITAR");
			}	
			
		}
		
		if(nextSequenceId == tareas.Rechazada && completo == 'true') {
			var motivoRechazo = hAPI.getCardValue("motivoRechazo");
			if (motivoRechazo == ""){
				throw "El motivo de rechazo es obligatorio.";
			}else{
				sendMail("RAF09-Rechazada");
			}
		}
		
		if(((tarea == tareas.RevisarCA && nextSequenceId == tareas.CompletarCA) || 
				(tarea == tareas.AuditarCA && nextSequenceId == tareas.RevisarCA)) 
				&& completo == 'true') {
			if (comentarios == ""){
				throw "Debe realizar una observaci√≥n para devolver la solicitud.";
			}
		}
		
		if(hAPI.getCardValue("solicitante") == "" && hAPI.getCardValue("area") == "" && hAPI.getCardValue("distrito") == ""){
			throw "No tiene permisos para iniciar una solicitud.";
		}
		
		actualizarCartaAcuerdo(ejb, nroProceso, estado);
		
	}
	
	function actualizarCartaAcuerdo(ejb, nroProceso, estado){
	
		var cartaAcuerdoDTO = ejb.newCartaAcuerdo();
		var sdf = new java.text.SimpleDateFormat("yyyy-MM-dd");
		var sdfMes = new java.text.SimpleDateFormat("MM/yyyy");
		var simpleDateFormat = new java.text.SimpleDateFormat("dd/MM/yyyy");
		
		var fechaSolicitud = hAPI.getCardValue("fechaSolicitud") != "" ? sdf.parse(hAPI.getCardValue("fechaSolicitud")) : null;
	
		cartaAcuerdoDTO.setNumeroSolicitud(java.lang.Integer.parseInt(nroProceso));
		cartaAcuerdoDTO.setFechaSolicitud(fechaSolicitud);
		cartaAcuerdoDTO.setSolicitante(hAPI.getCardValue("solicitante"));
		cartaAcuerdoDTO.setMatriculaSolicitante(hAPI.getCardValue("matriculaSolicitante"));
		cartaAcuerdoDTO.setMailGteDistrito(hAPI.getCardValue("mailGteDistrito"));
		cartaAcuerdoDTO.setNroInversionComercial(hAPI.getCardValue("nroInversionComercial"));
		cartaAcuerdoDTO.setAreaCodigo(hAPI.getCardValue("areaCodigo"));
		cartaAcuerdoDTO.setAreaDescripcion(hAPI.getCardValue("area"));
		cartaAcuerdoDTO.setDistritoCodigo(hAPI.getCardValue("distritoCodigo"));
		cartaAcuerdoDTO.setDistritoDescripcion(hAPI.getCardValue("distrito"));
		cartaAcuerdoDTO.setCodGrupoGteDistrito(hAPI.getCardValue("codGrupoGteDistrito"));   
		cartaAcuerdoDTO.setGrupoGteDistrito(hAPI.getCardValue("grupoGteDistrito"));
		cartaAcuerdoDTO.setNombreAPM(hAPI.getCardValue("nombreAPM"));
		cartaAcuerdoDTO.setMatriculaAPM(hAPI.getCardValue("matriculaAPM"));
		cartaAcuerdoDTO.setEmailAPM(hAPI.getCardValue("emailAPM"));
		cartaAcuerdoDTO.setFecha(hAPI.getCardValue("fecha") != "" ? simpleDateFormat.parse(hAPI.getCardValue("fecha")) : null);
		cartaAcuerdoDTO.setTipoInversion(hAPI.getCardValue("tipoInversion"));
		cartaAcuerdoDTO.setApellido(hAPI.getCardValue("apellido"));
		cartaAcuerdoDTO.setNombre(hAPI.getCardValue("nombre"));
		cartaAcuerdoDTO.setMesInversion(hAPI.getCardValue("mesInversion") != "" ? sdfMes.parse(hAPI.getCardValue("mesInversion")) : null);
		cartaAcuerdoDTO.setFormaPago(hAPI.getCardValue("formaPago"));
		cartaAcuerdoDTO.setCodFormaPago(hAPI.getCardValue("codFormaPago"));
		cartaAcuerdoDTO.setMotivoRechazo(hAPI.getCardValue("motivoRechazo"));
		cartaAcuerdoDTO.setTarea(hAPI.getCardValue("tarea"));
		cartaAcuerdoDTO.setGteDistrito(hAPI.getCardValue("gteDistrito"));
		
		log.info("****** Previo al save EJB");
		ejb.actualizarCartaAcuerdo(cartaAcuerdoDTO, estado);
		log.info("****** Fin del save Carta Acuerdo. Solicitud nro:"+getValue("WKNumProces"));
	
	}

	function sendMail(mailTemplate, comment) {
		var companyId = getValue("WKCompany");
		var nroProceso = getValue("WKNumProces");
		var changeReasonId = hAPI.getCardValue("changeReasonId");
		
		var mailTemplatesData = {
			"RAF09-Auditada": {
				getSubject: function() {
					return "Solicitud de Carta Acuerdo " + nroProceso + " aprobada";
				},
				getDestinatarios: function() {
					return getNotificationEmails(changeReasonId, "avisoNueva");
				}
			},
			"RAF09-Rechazada": {
				getSubject: function() {
					return "Solicitud de Carta Acuerdo " + nroProceso + " rechazada";
				},
				getDestinatarios: function() {
					return getNotificationEmails(changeReasonId, "avisoRechazada");
				}
			}
		};
		
		var mailTemplateData = mailTemplatesData[mailTemplate];
		var subject = mailTemplateData.getSubject();
		var destinatarios = mailTemplateData.getDestinatarios();
		
		var cardData = hAPI.getCardData(nroProceso);
		
		var tipoInversion;
		
		log.info(" ########################## RAF09-bts tipoInversion: " + cardData.get("tipoInversion"));
		
		if(cardData.get("tipoInversion") == "alojamientos") {
			tipoInversion = "ALOJAMIENTO CONGRESO O EVENTO CIENTIFICO";
		}else if(cardData.get("tipoInversion") == "aereosNac") {
			tipoInversion = "AEREO CONGRESO NACIONAL";
		}else if(cardData.get("tipoInversion") == "inscripciones") {
			tipoInversion = "INSCRIPCION A CONGRESO";
		}else if(cardData.get("tipoInversion") == "aereosInt") {
			tipoInversion = "AEREO CONGRESO INTERNACIONAL";
		}else if(cardData.get("tipoInversion") == "equipamientos") {
			tipoInversion = "EQUIPAMIENTO MEDICO";
		}else if(cardData.get("tipoInversion") == "becaNac") {
			tipoInversion = "BECA COMPLETA NACIONAL";
		}else if(cardData.get("tipoInversion") == "becaInt") {
			tipoInversion = "BECA COMPLETA INTERNACIONAL";
		}		

		var data = {		
			"gteDistrito": cardData.get("gteDistrito"),
			"numeroSolicitud": cardData.get("nroSolicitud"),
			"area": cardData.get("area"),
			"distrito": cardData.get("distrito"),
			"nombreAPM": capitalize(cardData.get("nombreAPM")),
			"tipoInversion": tipoInversion,
			"mesInversion": cardData.get("mesInversion"),
			"nombre": cardData.get("nombre"),
			"apellido": cardData.get("apellido"),
			"formaPago": cardData.get("formaPago"),
			"motivoRechazo": cardData.get("motivoRechazo"),
			"patrocinio": cardData.get("nroInversionComercial")
		}
		
		// Aca se envia el mail a los destinatarios.
		log.info("***** Enviando mail a destinatarios: " + destinatarios);
		for(var j in destinatarios){
			var mail = destinatarios[j];
			try{
				sendCustomEmail({
					companyId: companyId, 
					subject: subject, 
					from: "procesobpm@raffo.com.ar", 
					to: mail, 
					templateId: mailTemplate,
					templateDialect: "es",
					templateHtml: mailTemplate + ".html",
					datos: data
				});
			} catch (e){
				log.error("RAF09-beforeTaskSave - Error enviando mail a "+mail + " template: " + mailTemplate);
			}
		}
	}

	function getNotificationEmails(reasonName, fieldName){
		var mails = [];
		
		log.info("*** getNotificationEmails 1: ");
			
		mails.push(hAPI.getCardValue("mailGteDistrito"));
		
		log.info("*** mailGteDistrito: " + " mails: "+ mails);
		
		if(fieldName == "avisoRechazada"){		
			
 		    var usuariosDelGrupoGteArea = getUserList(hAPI.getCardValue("codGrupoGteArea"));
		    
		    for(var i = 0; i < usuariosDelGrupoGteArea.size(); i++) { 
		    	var mailsAux = getMailList(usuariosDelGrupoGteArea.get(i));
		    	 for(var j = 0; j < mailsAux.size(); j++) {  
		    		 mails.push(mailsAux.get(j));
		    	}
		    }

		    log.info("*** usuariosDelGrupoGteArea: " + " mails: "+ mails);
		}
		
		var usuariosAsistDistrito = getUserList(hAPI.getCardValue("codGrupoAsistDistrito"));
	    
	    for(var i = 0; i < usuariosAsistDistrito.size(); i++) { 
	    	var mailsAux = getMailList(usuariosAsistDistrito.get(i));
	    	 for(var j = 0; j < mailsAux.size(); j++) {  
	    		 mails.push(mailsAux.get(j));
	    	}
	    }

	    
	    log.info("*** usuariosAsistDistrito: " + " mails: "+ mails);
		
	    log.info("*** getNotificationEmails Area: " + hAPI.getCardValue("areaCodigo") + " mails:"+mails);
	    
	    return mails;
	}
	
	function createRequestFolder() {
		
		var arq = arqMarvinLoad("v1", {
			prop: "com.arquimeda.marvin.server.js.Properties-v1"
		});
		arq.prop.load({
			datasetName: "parametros"
		});
		
		var companyId = getValue("WKCompany"),
			nroProceso = getValue("WKNumProces"),
			userId = getValue("WKUser");
		
		log.info("**** Creando carpeta de la solicitud: " + nroProceso + " userId: " + userId + " companyId: " + companyId);
		
		var login = arq.prop.get("RAF09.usuarioSistema.login");
		var password = arq.prop.get("RAF09.usuarioSistema.pass");
		var sysCollegueId = arq.prop.get("RAF09.usuarioSistema.id");
		var carpetaAdjuntosId = arq.prop.get("RAF09.carpetaAdjuntos.docId");
		
		var requestFolder = "";	

		var codGrupoGteDistrito = hAPI.getCardValue("codGrupoGteDistrito");
		var codGrupoGteArea = hAPI.getCardValue("codGrupoGteArea");
		var codGrupoGtePromocion = hAPI.getCardValue("codGrupoGtePromocion");
		
			var securityArr = [];
			
			securityArr.push({
				permission: true, 
				attributionType: "GROUP", 
				attributionValue: codGrupoGteDistrito, 
				securityLevel: "READING"
			});
			
			securityArr.push({
				permission: true, 
				attributionType: "GROUP", 
				attributionValue: codGrupoGteArea, 
				securityLevel: "READING"
			});
			
			securityArr.push({
				permission: true, 
				attributionType: "GROUP", 
				attributionValue: codGrupoGtePromocion, 
				securityLevel: "READING"
			});
			
			securityArr.push({
				permission: true, 
				attributionType: "GROUP", 
				attributionValue: "RAF09-GTOR-PROCESO", 
				securityLevel: "READING"
			});
			
			securityArr.push({
				permission: true, 
				attributionType: "GROUP", 
				attributionValue: "RAF09-AUDITORIA", 
				securityLevel: "READING"
			});
			
			securityArr.push({
				permission: true, 
				attributionType: "ALL", 
				attributionValue: "all", 
				securityLevel: "ALL"
			});

			requestFolder = ged.createFolderIfNotExist({
				user: login,
				password: password,
				colleagueId: sysCollegueId,
				publisherId: userId,
				companyId: companyId,			
				searchName: nroProceso,
				name: nroProceso,
				additionalComments: "",
				parentId: carpetaAdjuntosId,
				inheritSecurity: false,
				security: securityArr
			});
			
			log.info("**** Carpeta de la solicitud " + nroProceso + " creada. DocId: " + requestFolder.getDocumentId());
			
			hAPI.setCardValue("carpetaAdjuntosId", requestFolder.getDocumentId());
		
		return requestFolder.getDocumentId();
		
	}

	function uploadAttachmentsToGed(parentFolderId){
		
		log.info("**** Cargando los documentos adjuntos al GED. Carpeta: " + parentFolderId);
		
		var calendar = java.util.Calendar.getInstance().getTime();
	    var docs = hAPI.listAttachments();
	    var attachmentsData = []
	    
	    for (var i = 0; i < docs.size(); i++) {
	        var doc = docs.get(i);
	        
	        if (doc.getDocumentType() != "7") {
	            continue;
	        }
	          
	        doc.setParentDocumentId(java.lang.Integer.parseInt(parentFolderId));
	        doc.setVersionDescription("RAF09: " + getValue("WKNumProces"));
	        doc.setExpires(false);
	        doc.setCreateDate(calendar);
	        doc.setInheritSecurity(true);
	        doc.setTopicId(1);
	        doc.setUserNotify(false);
	        doc.setValidationStartDate(calendar);
	        doc.setVersionOption("0");
	        doc.setUpdateIsoProperties(true);
	        
	        log.info("****** Subiendo el archivo: " + doc.getDocumentDescription());
	        hAPI.publishWorkflowAttachment(doc);
	        attachmentsData.push(doc.getDocumentId()+"___:___"+doc.getDocumentDescription());
	    }
	    
	    return attachmentsData;
	       
	}

function onNotify(subject, receivers, template, params){

	log.info("*****Ejecutando RAF09.onNotify(" + subject + ", " + receivers + ",  " + template + " , " + params + ");");
	
	if(template == "TPLCOMPLEMENT_AUTHORITY_TO_SUBSTITUTE" ||
			template == "TPLCOMPLEMENT_MANAGER_TO_SUBSTITUTE" ||
			template == "TPLCOMPLEMENT_REQUISITIONER_TO_SUBSTITUTE" ||
			template == "TPLCOMPLEMENT_AUTHORITY" ||
			template == "TPLCOMPLEMENT_MANAGER" ||
			template == "TPLCOMPLEMENT_REQUISITIONER" ||
			template == "TPLDOCUMENT_WATCH_MODIFY_CONTENT" ||
			template == "TPLDOCUMENT_VERSION_REMOVED_YOU_APPROVED"){ // Nuevo complemento 	
		
		log.info("***** RAF09.onNotify:" + template + " eliminando receivers..");
		receivers.clear();
		log.info("***** RAF09.onNotify: " + template + " TEMPLATE TPLATTACHMENT_REQUISITIONER: receivers eliminados...");
	}
	
	if(template == "TPLATTACHMENT_AUTHORITY_TO_SUBSTITUTE" ||
			template == "TPLATTACHMENT_MANAGER_TO_SUBSTITUTE" ||
			template == "TPLATTACHMENT_REQUISITIONER_TO_SUBSTITUTE" ||
			template == "TPLATTACHMENT_AUTHORITY" ||
			template == "TPLATTACHMENT_MANAGER" ||
			template == "TPLATTACHMENT_REQUISITIONER" ||
			template == "TPLUPDATE_ATTACHMENT_REQUISITIONER"){ // Nuevo documento adjunto
		
		log.info("***** RAF09.onNotify: " + template + ": eliminando receivers..");
		receivers.clear();
		log.info("***** RAF09.onNotify: " + template + ": receivers eliminados...");
	}

	
	log.info("*****Fin Ejecucion RAF09.onNotify");
}

var ged = {
	attributionTypes: {
		"USER": 1,
		"GROUP": 2,
		"ALL": 3 
	},
	securityLevels: {
		"NONE": null,
		"READING": 0,
		"RECORDING": 1,
		"MODIFICATION": 2,
		"ALL": 3		
	},
	
	createSimpleFolder: function(params){
		var opts = this.simpleMerge({
			name: "Nueva Carpeta",
			additionalComments: "Nueva Carpeta",
			parentId: "0",
			inheritSecurity: true,
			security: [], // [{permission:true, attributionType:"USER", attributionValue:"adm", securityLevel:"ALL"}]
		}, params);
		
		//Instancia um novo documento e define as propriedades b√°sicas
    	var dto = docAPI.newDocumentDto();
        dto.setDocumentDescription(opts.name);
        dto.setAdditionalComments(opts.additionalComments);
        dto.setDocumentType("1");
        dto.setParentDocumentId(parseInt(opts.parentId));
        dto.setInheritSecurity(opts.inheritSecurity);
        
        //Definindo permiss√£os
    	var dtosSecurity = [];
    	
    	for(var i=0; i<opts.security.length; i++){
    		var dtoConfigSecurity = docAPI.newDocumentSecurityConfigDto();
    		
    		dtoConfigSecurity.setPermission(opts.security[i].permission);
    		dtoConfigSecurity.setShowContent(true);
    		dtoConfigSecurity.setAttributionType(this.attributionTypes[opts.security[i].attributionType]);
    		dtoConfigSecurity.setAttributionValue(opts.security[i].attributionValue);
    		dtoConfigSecurity.setSecurityLevel(this.securityLevels[opts.security[i].securityLevel]);    
    		
    		dtosSecurity.push(dtoConfigSecurity); 
    	}  	
        log.info("DEBUG - parentId " + opts.parentId);
        log.info("DEBUG - name " + opts.name);
        return docAPI.createFolder(dto, dtosSecurity, null);
	},
	
	// Busca si existe una carpeta y si no la crea
	// Busca por nombre de carpeta y "padre"
	// Si se define serchName busca por ese valor
	createFolderIfNotExist: function(params){
		var opts = this.simpleMerge({
			user: "adm", //login do usu√°rio.
			password: "adm", //senha do usu√°rio.
			companyId: "1", //c√≥digo da empresa.
			colleagueId: "adm",
			publisherId: "adm",
		
			searchName: false,  // patr√≥n del nombre
			
			name: "Nueva Carpeta",
			additionalComments: "Nueva Carpeta",
			parentId: "0",
			inheritSecurity: true,
			security: [], // [{permission:true, attributionType:"USER", attributionValue:"adm", securityLevel:"ALL"}]
			
			workflowPackage: "com.totvs.technology.ecm.dm.ws",
			workflowServiceName: "ECMFolderService",
		}, params);
		
		log.info("*** ged createFolderIfNotExist: buscando carpeta parentId:" + opts.parentId 
					+ " name:" + opts.name 
					+ " searchName:" + opts.searchName);
		
		var ret = ged.findFolder({
			companyId: opts.companyId,
			name: opts.searchName? opts.searchName : opts.name,
			parentId: opts.parentId
		});
		
		if (ret != null) {
			log.info("*** ged createFolderIfNotExist: carpeta encontrada ! parentId:" + opts.parentId 
					+ " name:" + opts.name 
					+ " searchName:" + opts.searchName
					+ " documentId:" + ret.getDocumentId()
					);
			return ret;
		} else {
			log.info("*** ged createFolderIfNotExist: carpeta NO encontrada ! parentId:" + opts.parentId 
					+ " name:" + opts.name 
					+ " searchName:" + opts.searchName
					+ " - Creando carpeta ..."
					);			
			return ged.createFolder(params);
		}
		
	},
	findFolder: function(params){
		var opts = this.simpleMerge({
			companyId: "1", //c√≥digo da empresa.
			name: "Nombre", // nombre del 
			parentId: "0",
			jndiName: "java:/jdbc/FluigDS",
		}, params);
		
		var conn = null,
			pstmt = null,
			documentId = null,
			version = null;
		
		try {
			
			if (opts.log) log.info("** Obteniendo conexion " + opts.jndiName);
			
			conn = new javax.naming.InitialContext().lookup(opts.jndiName).getConnection();   
	        
			var sql =	"SELECT [NR_DOCUMENTO] AS documentId," +
						"       [NR_VERSAO] AS version" +
						"  FROM [dbo].[DOCUMENTO] d " +
						" WHERE [d].[COD_EMPRESA] = ? " +
						"   AND [d].[LOG_DELETE] = 0 " +
						"   AND [d].[NR_DOCUMENTO_PAI] = ?" +
						"   AND [d].[VERSAO_ATIVA] = 1" +
						"   AND [d].[TP_DOCUMENTO] = 1" +
						"   AND [DS_PRINCIPAL_DOCUMENTO] LIKE ?" +
						"  ORDER BY [NR_DOCUMENTO] DESC";
			
						
			pstmt = conn.prepareStatement(sql);
			pstmt.setString(1, opts.companyId);
			pstmt.setString(2, opts.parentId);
			pstmt.setString(3, opts.name);
			
			var rs = pstmt.executeQuery();
			while (rs.next()) {
				documentId = rs.getString("documentId");
				version = rs.getString("version");	
			}

		} catch (ex) {
			log.error("*** ged findFolder: " + ex);
	    } finally {
	        if (pstmt != null) { pstmt.close(); }
	        if (conn != null) { conn.close(); }
	    }
		
	    if (documentId == null){
	    	return null;
	    } else {
	    	return {
	    		getDocumentId: function(){return documentId;},
	    		getVersion: function(){return version;}
	    	};
	    }
		
	},	
	createFolder: function(params){
		var opts = this.simpleMerge({
			user: "adm", //login do usu√°rio.
			password: "adm", //senha do usu√°rio.
			companyId: "1", //c√≥digo da empresa.
			colleagueId: "adm",
			publisherId: "adm",
			
			name: "Nueva Carpeta",
			additionalComments: "Nueva Carpeta",
			parentId: "0",
			inheritSecurity: true,
			security: [], // [{permission:true, attributionType:"USER", attributionValue:"adm", securityLevel:"ALL"}]
			
			workflowPackage: "com.totvs.technology.ecm.dm.ws",
			workflowServiceName: "ECMFolderService",
		}, params);
		
		var serviceHelper = ServiceManager.getService(opts.workflowServiceName).getBean(),
		serviceLocator = serviceHelper.instantiate(opts.workflowPackage + ".ECMFolderServiceService"),
		service = serviceLocator.getFolderServicePort();
		
		// Configuro el documento
		var documentDtoArr = serviceHelper.instantiate(opts.workflowPackage + ".DocumentDtoArray");
		
		var documentDto = serviceHelper.instantiate(opts.workflowPackage + ".DocumentDto");
		documentDto.setColleagueId(opts.colleagueId);
		documentDto.setCompanyId(parseInt(opts.companyId));
		documentDto.setDeleted(false);
		documentDto.setDocumentDescription(opts.name);
		documentDto.setAdditionalComments(opts.additionalComments);
		documentDto.setDownloadEnabled(true);
		documentDto.setInheritSecurity(opts.inheritSecurity);
		documentDto.setParentDocumentId(parseInt(opts.parentId));
		documentDto.setPublisherId(opts.publisherId);
		documentDto.setDocumentType("1");
		documentDto.setIconId(0);
	
		documentDtoArr.getItem().add(documentDto);

		// Configuro seguridad
		var securityDtoArr = serviceHelper.instantiate(opts.workflowPackage + ".DocumentSecurityConfigDtoArray");;
		for (var i=0; i<opts.security.length; i++) {
			var securityDto = serviceHelper.instantiate(opts.workflowPackage + ".DocumentSecurityConfigDto");
			securityDto.setPermission(opts.security[i].permission);
			securityDto.setShowContent(true);
			securityDto.setAttributionType(this.attributionTypes[opts.security[i].attributionType]);
			securityDto.setAttributionValue(opts.security[i].attributionValue);
			securityDto.setSecurityLevel(this.securityLevels[opts.security[i].securityLevel]);  
			securityDtoArr.getItem().add(securityDto);					
		}
		
		// Configuro Aprobadores
		var approversDtoArr = serviceHelper.instantiate(opts.workflowPackage + ".ApproverDtoArray");

		var webServiceMessageArray = serviceHelper.instantiate(opts.workflowPackage + ".WebServiceMessageArray");
		webServiceMessageArray = service.createFolder(opts.user, opts.password, parseInt(opts.companyId), documentDtoArr, securityDtoArr, approversDtoArr);
		return webServiceMessageArray.getItem().get(0);
	},
	
	updateFolder: function(params){
		var opts = this.simpleMerge({
			user: "adm", //login do usu√°rio.
			password: "adm", //senha do usu√°rio.
			companyId: "1", //c√≥digo da empresa.
			colleagueId: "adm",
			docId: "0",
			
			name: "",
			additionalComments: "",
			parentId: "0",
			inheritSecurity: true,
			security: [], // [{permission:true, attributionType:"USER", attributionValue:"adm", securityLevel:"ALL"}]
			
			workflowPackage: "com.totvs.technology.ecm.dm.ws",
			workflowServiceName: "ECMFolderService",
		}, params);
		
		var serviceHelper = ServiceManager.getService(opts.workflowServiceName).getBean(),
		serviceLocator = serviceHelper.instantiate(opts.workflowPackage + ".ECMFolderServiceService"),
		service = serviceLocator.getFolderServicePort();
		
		var documentDtoArr = service.getFolder(opts.user, opts.password, parseInt(opts.companyId), parseInt(opts.docId), opts.colleagueId);
		var securityDtoArr = service.getSecurity(opts.user, opts.password, parseInt(opts.companyId), parseInt(opts.docId));
		var approversDtoArr = serviceHelper.instantiate(opts.workflowPackage + ".ApproverDtoArray");
		
		var itemsToRemove = [];
		for (var i=0; i<opts.security.length; i++) {
			var index = null;
			for (var j=0; j<securityDtoArr.getItem().size(); j++) {
				var exists = opts.security[i].permission == securityDtoArr.getItem().get(j).isPermission() &&
							 this.attributionTypes[opts.security[i].attributionType] == securityDtoArr.getItem().get(j).getAttributionType() &&
							 opts.security[i].attributionValue == securityDtoArr.getItem().get(j).getAttributionValue();
				if (exists) index = j;
			}
				
			if (index != null) {
				if(this.securityLevels[opts.security[i].securityLevel] == null) {
					itemsToRemove.push(securityDtoArr.getItem().get(index));
				} else {
					securityDtoArr.getItem().get(index).setSecurityLevel(this.securityLevels[opts.security[i].securityLevel]);
				}
			} else {
				if(this.securityLevels[opts.security[i].securityLevel] != null) {
					var securityDto = serviceHelper.instantiate(opts.workflowPackage + ".DocumentSecurityConfigDto");
					securityDto.setPermission(opts.security[i].permission);
					securityDto.setShowContent(true);
					securityDto.setAttributionType(this.attributionTypes[opts.security[i].attributionType]);
					securityDto.setAttributionValue(opts.security[i].attributionValue);
					securityDto.setSecurityLevel(this.securityLevels[opts.security[i].securityLevel]);  
					securityDtoArr.getItem().add(securityDto);					
				}
			}
		}
		for (var i=0; i<itemsToRemove.length; i++) {
			securityDtoArr.getItem().remove(itemsToRemove[i]);
		}
		
		var webServiceMessageArray = serviceHelper.instantiate(opts.workflowPackage + ".WebServiceMessageArray");
		webServiceMessageArray = service.updateFolder(opts.user, opts.password, parseInt(opts.companyId), documentDtoArr, securityDtoArr, approversDtoArr);
		return webServiceMessageArray.getItem().get(0);
	},
	
	simpleMerge: function (obj1,obj2) {
		// realizar merge solo del 1er nivel de atributos
		var obj3 = {};    
		for (attrname in obj1) { 
			obj3[attrname] = obj1[attrname]; 
		}    
		for (attrname in obj2) { 
			obj3[attrname] = obj2[attrname]; 
		}    
		return obj3;
	}
};

